void put_figure_sht(struct SHEET *sht, int start_x, int start_y, char* buf,int buf_x, int buf_y, int bgc, int main_c, int turn)
{
	int k,l;
	int pos=0;

	for(k=0;k<buf_x;k++)
	{
		for(l=0;l<buf_y;l++)
		{
			pos=(start_y+k)*sht->bxsize+start_x+l;
			if(turn == 2)			pos=(start_y+k)*sht->bxsize+start_x+buf_x-l;
			else if(turn == -1)		pos=(start_y+l)*sht->bxsize+start_x+k;
			else if(turn == 1)		pos=(start_y+buf_y-1-l)*sht->bxsize+start_x+k;
			else if(turn == -2);

			if(buf[k*buf_y+l]=='0')
				sht->buf[pos]=bgc;
			else if(buf[k*buf_y+l]=='1')
				sht->buf[pos]=main_c;
			else if(buf[k*buf_y+l]=='2')
				sht->buf[pos]=THRID_C;
			else if(buf[k*buf_y+l]=='3')
			{
				if(pac_changing==1||dead)sht->buf[pos]=COL8_000000;
				else sht->buf[pos]=main_c;
			}
		}
	}
}
