void check_clock()
{
	if(gloabl_sec==60)
	{
		gloabl_sec=0;
		gloabl_min++;
		clock_change=3;
		if(gloabl_min==60)
		{
			gloabl_min=0;
			gloabl_hour++;
			clock_change=0;
			if(gloabl_hour==24)
			{
				gloabl_hour=0;
			}
		}
	}
}
