void put_a_map(struct SHEET*sht)
{
	int map_x0=(sht->bxsize/2)-500;
	int map_y0=(sht->bysize/2)-340;
	int map_x1=(sht->bxsize/2)+300;
	int map_y1=(sht->bysize/2)+360;

	int cube_1_hight	=50;
	int cube_1_length	=680;
	int cube_2_hight	=360;
	int cube_2_length	=50;

	int xsize=sht->bxsize;
	int ysize=sht->bysize;
	int map_c=COL8_FFFFFF;

	boxfill8(sht->buf, 	xsize, 	COL8_FFFFFF, 	(sht->bxsize)/2-500,	(sht->bysize)/2-332,	(sht->bxsize)/2+500,	(sht->bysize)/2+332);
	boxfill8(sht->buf, 	xsize, 	299, 					(sht->bxsize)/2+200,	(sht->bysize)/2-302,	(sht->bxsize)/2+450,	210);

	boxfill8(sht->buf,	xsize,	295,					2,																			22,													xsize-3,									ysize-3);
	boxfill8(sht->buf,	xsize,	COL8_FFFFFF,	map_x0-2,																map_y0-2,		 								map_x1+2,									map_y1+2);																//map background
	boxfill8(sht->buf,	xsize,	299,					map_x0,																	map_y0,											map_x1,										map_y1);																	//map background
	boxfill8(sht->buf,	xsize,	map_c,				map_x0+60,															map_y0+60,									map_x0+60+cube_1_length,	map_y0+60+cube_1_hight);									//up long cube
	boxfill8(sht->buf,	xsize,	map_c,				map_x0+60,															map_y1-60-cube_1_hight,			map_x0+60+cube_1_length,	map_y1-60);																//down long cube
	boxfill8(sht->buf,	xsize,	map_c,				map_x0+60,															map_y0+60+cube_1_hight+60,	map_x0+60+cube_2_length,	map_y0+60+cube_1_hight+60+cube_2_hight);	//left high cube
	boxfill8(sht->buf,	xsize,	map_c,				map_x0+60+cube_1_length-cube_2_length,	map_y0+60+cube_1_hight+60,	map_x0+60+cube_1_length,	map_y0+60+cube_1_hight+60+cube_2_hight);	//right high cube

	int cube_3_hight	=50;
	int cube_3_length	=100;
	int cube_4_hight	=50;
	int cube_4_length	=50;

	boxfill8(sht->buf,	xsize,map_c, 				map_x0+60+cube_2_length+60, 							map_y0+60+cube_1_hight+60, 								map_x0+60+cube_2_length+60+cube_3_length, 						map_y0+60+cube_1_hight+60+cube_3_hight);
	boxfill8(sht->buf,	xsize,map_c, 				map_x0+60+cube_2_length+60, 							map_y0+60+cube_1_hight+60+cube_3_hight, 	map_x0+60+cube_2_length+60+cube_4_length, 						map_y0+60+cube_1_hight+60+cube_3_hight+cube_4_hight);

	boxfill8(sht->buf,	xsize,map_c, 				map_x1-60-cube_2_length-60-cube_3_length, map_y0+60+cube_1_hight+60,  			  			map_x1-60-cube_2_length-60,														map_y0+60+cube_1_hight+60+cube_3_hight);
	boxfill8(sht->buf,	xsize,map_c, 				map_x1-60-cube_2_length-60-cube_4_length, map_y0+60+cube_1_hight+60+cube_3_hight,  	map_x1-60-cube_2_length-60,  													map_y0+60+cube_1_hight+60+cube_3_hight+cube_4_hight);

	boxfill8(sht->buf,	xsize,map_c, 				map_x0+60+cube_2_length+60, 							map_y1-60-cube_1_hight-60-cube_3_hight-cube_4_hight, map_x0+60+cube_2_length+60+cube_4_length, 	map_y1-60-cube_1_hight-60-cube_3_hight);
	boxfill8(sht->buf,	xsize,map_c, 				map_x0+60+cube_2_length+60,								map_y1-60-cube_1_hight-60-cube_3_hight,		map_x0+60+cube_2_length+60+cube_3_length, 						map_y1-60-cube_1_hight-60);

	boxfill8(sht->buf,	xsize,map_c, 				map_x1-60-cube_2_length-60-cube_3_length, map_y1-60-cube_1_hight-60-cube_3_hight, 	map_x1-60-cube_2_length-60, 													map_y1-60-cube_1_hight-60);
	boxfill8(sht->buf,	xsize,map_c, 				map_x1-60-cube_2_length-60-cube_4_length, map_y1-60-cube_1_hight-60-cube_3_hight-cube_4_hight,   map_x1-60-cube_2_length-60, 							map_y1-60-cube_1_hight-60-cube_3_hight);

}
